import React, { Component } from 'react';
import './App.css';
import FilmListing from './FilmListing';
import FilmDetails from './FilmDetails';
import TMDB from './TMDB';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      listing: {
        title: TMDB.films.map(film => film.title),
        release_date: TMDB.films.map(film => film.release_date)
      },
      details: {
        overview: TMDB.films.map(film => film.overview)
      }
    }
  }

  render() {
    return (
      <div>
        <FilmListing title={this.state.listing}/>
        <FilmDetails details={this.state.details}/>
      </div>
    );
  }
}

export default App;
